class ImmutableList:
    def __init__(self, elements):
        self.elements = list(elements)

    def length(self):
        return len(self.elements)

    def contains(self, element):
        return element in self.elements

    def index_of(self, element):
        return self.elements.index(element)

    def count_of(self, element):
        return self.elements.count(element)

    def sub_list(self, start_index, end_index):
        return self.elements[start_index:end_index]

    def concat(self, other_list):
        return self.elements + other_list.elements

    def reverse(self):
        return self.elements[::-1]

    def sort(self):
        return sorted(self.elements)

    def remove_all(self, element):
        return [x for x in self.elements if x != element]

    def insert_at(self, index, element):
        return self.elements[:index] + [element] + self.elements[index:]

    def replace_at(self, index, element):
        return self.elements[:index] + [element] + self.elements[index + 1:]

